sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	gs.addInfoMessage('in run cs code inpsect');
	var codeInspectionEnabled = (gs.getProperty("x_8821_code.allow.code.inspection")=="true") || false;
	if(codeInspectionEnabled){
		gs.addInfoMessage('passes property check');
		var t = new x_8821_code.Analysis();
		try {
			var outputArr = t._getClientScripts(current.getValue('sys_id'));
			/*
			returnObj.businessRules = this._getBusinessRules(); aa
			returnObj.scriptIncludes = this._getScriptIncludes();
			returnObj.uiPolicies = this._getUIPoliciesScripts();
			returnObj.clientScripts = this._getClientScripts();
			*/
			if(outputArr.length>0) {
				for(var x = 0;x<outputArr[0].errors.length;x++){
					gs.addErrorMessage(outputArr[0].errors[x]);
				}
			}
		} catch(e) {
			gs.addInfoMessage(JSON.stringify(e));
		}
	}
})(current, previous);
